import React, { useEffect, useState } from "react";
import "./input.css";
import "./App.css";

function App() {
  const targetDate = new Date("2023-06-07T20:30:00");
  const [timeRemaining, setTimeRemaining] = useState(calculateTimeRemaining());
  function calculateTimeRemaining() {
    const now = new Date();
    const difference = targetDate.getTime() - now.getTime();

    if (difference <= 0) {
      // O cronômetro já chegou ao destino
      return {
        days: 0,
        hours: 0,
        minutes: 0,
        seconds: 0,
      };
    }

    const days = Math.floor(difference / (1000 * 60 * 60 * 24));
    const hours = Math.floor((difference / (1000 * 60 * 60)) % 24);
    const minutes = Math.floor((difference / 1000 / 60) % 60);
    const seconds = Math.floor((difference / 1000) % 60);

    return {
      days,
      hours,
      minutes,
      seconds,
    };
  }

  useEffect(() => {
    const timer = setInterval(() => {
      setTimeRemaining(calculateTimeRemaining());
    }, 1000);

    return () => {
      clearInterval(timer);
    };
  }, []);
  const [clipPathId, setClipPathId] = useState("");

  useEffect(() => {
    // Gera um ID único para o elemento <clipPath>
    const id = `clipPath${Math.random().toString(36).substr(2, 9)}`;
    setClipPathId(id);
  }, []);

  return (
    <div className="min-h-screen w-full flex items-center justify-center page-cronometro px-8 py-8">
      <div className="ww-96 bg-white rounded-lg p-4 border-2 border-black-200 px-8">
        <h1 className="font-sans">
          Contagem regressiva para o nosso primeiro encontro!
          <strong className="italic">17 maio 2023</strong>
        </h1>
        <div className="heart-progress">
          <svg viewBox="0 0 100 100" className="heart-svg">
            <clipPath id={clipPathId}>
              <path
                d="M50 85c-15.474 0-28-12.526-28-28 0-12.7 8.26-22.09 21.925-27.924 7.208-2.687 14.864-.607 21.075 4.883L50 32.1l5.973-5.141c6.21-5.49 13.867-7.57 21.074-4.883C69.74 34.91 78 44.3 78 57c0 15.474-12.526 28-28 28zm0-2c14.336 0 26-11.663 26-26 0-11.163-7.242-19.368-19.425-24.91l-3.108-1.15L50 27.1l-3.468-2.16-3.108 1.15C31.242 37.632 24 45.837 24 57c0 14.336 11.663 26 26 26zm0 0"
                fill="#ff0000"
              />
            </clipPath>
            <rect
              x="0"
              y="0"
              width={10000000}
              height="100"
              fill="#ff0000"
              clipPath={`url(#${clipPathId})`}
            />
          </svg>
        </div>
        <div className="lista-horas flex">
          <p className="font-sans">
            Days: <strong>{timeRemaining.days}</strong>
          </p>
          <p className="font-sans">
            Hours: <strong>{timeRemaining.hours}</strong>
          </p>
          <p className="font-sans">
            Minutes: <strong>{timeRemaining.minutes}</strong>
          </p>
          <p className="font-sans">
            Seconds: <strong>{timeRemaining.seconds}</strong>
          </p>
        </div>
        <p className="names italic">Valleir e Emely!</p>
      </div>
    </div>
  );
}

export default App;
