import React, { useEffect, useState } from "react";
import "./input.css";
import "./App.css";

function App() {
  const targetDate = new Date("2023-06-07T20:30:00");
  const [timeRemaining, setTimeRemaining] = useState(calculateTimeRemaining());
  function calculateTimeRemaining() {
    const now = new Date();
    const difference = targetDate.getTime() - now.getTime();

    if (difference <= 0) {
      // O cronômetro já chegou ao destino
      return {
        days: 0,
        hours: 0,
        minutes: 0,
        seconds: 0,
      };
    }

    const days = Math.floor(difference / (1000 * 60 * 60 * 24));
    const hours = Math.floor((difference / (1000 * 60 * 60)) % 24);
    const minutes = Math.floor((difference / 1000 / 60) % 60);
    const seconds = Math.floor((difference / 1000) % 60);

    return {
      days,
      hours,
      minutes,
      seconds,
    };
  }

  useEffect(() => {
    const timer = setInterval(() => {
      setTimeRemaining(calculateTimeRemaining());
    }, 1000);

    return () => {
      clearInterval(timer);
    };
  }, []);
  return (
    <div className="min-h-screen w-full flex items-center justify-center page-cronometro">
      <div className="w-3/6 bg-white rounded-lg p-4">
        <h1 className="font-mono text-lg  text-4xl text-slate-400 text-center">
          Valleir e Emyh
        </h1>
        <div className="flex items-center justify-cente rid-cols-5">
          <p>Days: {timeRemaining.days}</p>
          <p>Hours: {timeRemaining.hours}</p>
          <p>Minutes: {timeRemaining.minutes}</p>
          <p>Seconds: {timeRemaining.seconds}</p>
        </div>
      </div>
    </div>
  );
}

export default App;
